import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'hola mundo';
  public counter:number = 10;


  increaseBy(value: number): void {
      this.counter += value;
  }

  resetCounter() :void{
    this.counter = 10;
  }
}

