import { Component, Input } from '@angular/core';
import { Character } from '../../interfaces/character.interface';

@Component({
  selector: 'dbz-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css'],
  standalone: false
})
export class ListComponent {

  @Input()
  public characterList: Character[] = [{
    name:'Trunks',
    power: 1000
  },
  {
    name: 'Lizeth',
    power: 0
  }
];
}
<h4>Listado</h4>
<ul class="list-group">
  <li *ngFor="let character of characterList;
    index as i;
    last as isLast;
    first as isFirst;
    even as isEven;
    odd as isOdd;
    "
      class="list-group-item d-flex justify-content-between"
      [ngClass]=" {
        'list-group-item-dark': isLast,
        'list-group-item-primary': isEven
      }"
      >
      <div>

        <span class="text-primary"> {{ i + 1}} </span>
        <span>{{character.name}} - </span>
        <strong>Power: </strong>
        <span> {{character.power}} </span>
      </div>
        <button class="btn btn-sm btn-outline-danger ml-2">X</button>
    </li>
</ul>
