import { Component, EventEmitter, Output } from '@angular/core';
import { Character } from '../../interfaces/character.interface';
import { compileNgModule } from '@angular/compiler';

@Component({
  selector: 'dbz-add-character',
  templateUrl: './add-character.component.html',
  styleUrls: ['./add-character.component.css']
})
export class AddCharacterComponent {

  @Output()
  public oneNewCharacter: EventEmitter<Character> = new EventEmitter();
  public character: Character = {
    name: '',
    power: 0,
  }

  emitCharacter():void {

    // debugger;
    // console.log(this.character);
    if ( this.character.name.length === 0) return;

    this.oneNewCharacter.emit(this.character);
    this.character.name = '';
    this.character.power = 0;
  }
}
<h4>Agregar Personaje</h4>
    <form class="row" (ngSubmit)="emitCharacter()">

      <input
      type="text"
      [(ngModel)]="character.name"
      name="name"
      class="form-control mb-2"
      placeholder="Nombre">

      <input
      type="number"
      name="power"
      [(ngModel)]="character.power"
      class="form-control mb-2"
      placeholder="Poder">

      <button type="submit" class="btn btn-outline-primary">
        Agregar
      </button>

      <pre>{{ character | json }}</pre>
    </form>
