import { Component } from '@angular/core';

@Component({
  selector: 'app-heroes-list',
  templateUrl: './heroes-list.component.html',
  styleUrls: ['./heroes-list.component.css']
})
export class HeroesListComponent {
  public heroNames: string[] = ['Spiderman', 'Ironman', 'Hulk', 'She Hulk', 'Thor'];

  public deletedHero?: string;

  removeLastHero():void {


    this.deletedHero = this.heroNames.at(-1);
    this.heroNames.pop();
    // this.heroNames = this.heroNames.slice(0,-1);
    // console.log(newHeroNames);
  }

  resetHeroes():void {
    this.heroNames = ['Spiderman', 'Ironman', 'Hulk', 'She Hulk', 'Thor'];
    this.deletedHero = undefined;
  }
}
<h3>Listado de Heroes</h3>

<div *ngIf="deletedHero">

  <h3>Heroe borrado <small class="text-danger">{{ deletedHero }}</small></h3>
</div>
<ng-template>
  <h3>No he borrado nada</h3>

</ng-template>

<div>

</div>

<button (click)="removeLastHero()" class="btn btn-outline-danger">
  Borrar Ultimo Heroe
</button>

<button (click)="resetHeroes()" class="btn btn-outline-primary">
 Reset
</button>

<ul class="mt-2 list-group">
  <li
    *ngFor="let name of heroNames"
    class="list-group-item">
    {{ name }}
  </li>
</ul>
